def create_restore_point():
	out, err = subprocess.Popen("powershell.exe vssadmin resize shadowstorage /on=c: /for=c: /maxsize=5000MB", shell=True, stdout=subprocess.PIPE, stderr=subprocess.PIPE).communicate()
	out, err = subprocess.Popen("powershell.exe -ExecutionPolicy Bypass -Command \"Checkpoint-Computer -Description 'BeforeSecurityHardening' -RestorePointType 'MODIFY_SETTINGS'\"", shell=True, stdout=subprocess.PIPE, stderr=subprocess.PIPE).communicate()
	out, err = subprocess.Popen("reg add \"HKLM\\SOFTWARE\\Microsoft\\Windows NT\\CurrentVersion\\SystemRestore\" /v SystemRestorePointCreationFrequency /t REG_DWORD /d 20 /f", shell=True, stdout=subprocess.PIPE, stderr=subprocess.PIPE).communicate()
	out, err = subprocess.Popen("reg add HKEY_LOCAL_MACHINE\\Software\\Microsoft\\OLE /v EnableDCOM /t REG_SZ /d N /F", shell=True, stdout=subprocess.PIPE, stderr=subprocess.PIPE).communicate()
	out, err = subprocess.Popen("powershell.exe enable-computerrestore -drive c:\\", shell=True, stdout=subprocess.PIPE, stderr=subprocess.PIPE).communicate()

